 mix[i] = this.param[i];
 }

 // mix rounds //
 for (i = 0; i < this.rounds; i += 2) {
 mix[4] = (mix[4] ^ this.rotl(mix[0] + mix[12], 7)) >>> 0;
 mix[8] = (mix[8] ^ this.rotl(mix[4] + mix[0], 9)) >>> 0;
 mix[12] = (mix[12] ^ this.rotl(mix[8] + mix[4], 13)) >>> 0;
